// src/pages/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import NavBar from './NavBar';
import Footer from './Footer';
import BackgroundAnimation from '..BackgroundAnimation';

const Home = () => {
  return (
    <div className="relative min-h-screen flex flex-col">
      {/* Background Animation */}
      <BackgroundAnimation />

      {/* Navbar */}
      <NavBar />

      {/* Hero Section */}
      <main className="hero flex-grow flex flex-col items-center justify-center bg-gradient-to-r from-blue-200 via-blue-100 to-blue-300 p-8">
        <div className="hero-content flex flex-col items-center gap-8 text-center z-10">
          <div className="hero-text">
            <h2 className="text-3xl md:text-4xl font-bold text-blue-700">
              Building modern, scalable web applications
            </h2>
            <p className="mt-4 text-lg text-blue-600">
              Welcome! I'm Mohamed Ali, a developer passionate about creating clean and efficient code. Explore my work and skills below.
            </p>
            <Link
              to="/projects"
              className="mt-6 bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-300"
            >
              View My Work
            </Link>
          </div>
          <div className="hero-image">
            <img
              src="/Images/chen-Qtu3hGinLF8-unsplash.jpg"
              alt="Coding at Work"
              className="max-w-sm rounded-lg shadow-lg"
            />
          </div>
        </div>
      </main>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default Home;
