// src/components/BackgroundAnimation.js
import React from 'react';
import { motion } from 'framer-motion';

const BackgroundAnimation = () => {
  return (
    <motion.div
      className="absolute inset-0 -z-10 bg-gradient-to-r from-purple-600 via-teal-500 to-pink-400 overflow-hidden"
      initial={{ opacity: 0 }}
      animate={{ opacity: 0.2 }}
      transition={{ duration: 2 }}
    >
      <div className="relative w-full h-full">
        {/* Layer 1 - Large Circles */}
        <motion.div
          className="absolute w-60 h-60 rounded-full bg-purple-500 opacity-60"
          animate={{ x: [0, 300, -200, 0], y: [0, -250, 250, 0] }}
          transition={{ duration: 15, repeat: Infinity, ease: "easeInOut" }}
          style={{ top: '15%', left: '10%' }}
        />
        <motion.div
          className="absolute w-72 h-72 rounded-full bg-teal-700 opacity-40"
          animate={{ x: [-100, -300, 150, -50], y: [100, -200, 100, 50] }}
          transition={{ duration: 20, repeat: Infinity, ease: "easeInOut" }}
          style={{ top: '70%', left: '80%' }}
        />

        {/* Layer 2 - Medium Circles */}
        <motion.div
          className="absolute w-36 h-36 rounded-full bg-pink-400 opacity-50"
          animate={{ x: [50, -150, 200, 0], y: [-50, 200, -200, 100] }}
          transition={{ duration: 10, repeat: Infinity, ease: "easeInOut" }}
          style={{ top: '30%', left: '60%' }}
        />
        <motion.div
          className="absolute w-40 h-40 rounded-full bg-purple-700 opacity-50"
          animate={{ x: [-80, 150, -100, 80], y: [80, -100, 50, -50] }}
          transition={{ duration: 12, repeat: Infinity, ease: "easeInOut" }}
          style={{ top: '50%', left: '20%' }}
        />

        {/* Layer 3 - Small Pulsating Circles */}
        <motion.div
          className="absolute w-20 h-20 rounded-full bg-teal-500 opacity-70"
          animate={{ scale: [1, 1.2, 1], opacity: [0.5, 0.7, 0.5] }}
          transition={{ duration: 5, repeat: Infinity, ease: "easeInOut" }}
          style={{ top: '20%', left: '50%' }}
        />
        <motion.div
          className="absolute w-24 h-24 rounded-full bg-pink-600 opacity-60"
          animate={{ scale: [1, 1.3, 1], opacity: [0.4, 0.6, 0.4] }}
          transition={{ duration: 6, repeat: Infinity, ease: "easeInOut" }}
          style={{ top: '80%', left: '40%' }}
        />
      </div>
    </motion.div>
  );
};

export default BackgroundAnimation;
