import React from 'react';
import { motion } from 'framer-motion';

const BackgroundAnimation = () => {
  const variants = {
    hidden: { opacity: 0 },
    visible: { opacity: 0.15, transition: { duration: 3, yoyo: Infinity } },
  };

  return (
    <motion.div
      className="absolute inset-0 -z-10 bg-gradient-to-r from-blue-200 via-blue-100 to-blue-300"
      initial="hidden"
      animate="visible"
      variants={variants}
    >
      <div className="relative w-full h-full">
        <motion.div
          className="absolute w-32 h-32 rounded-full bg-blue-300"
          animate={{ x: [0, 200, 0], y: [0, -200, 0] }}
          transition={{ duration: 6, repeat: Infinity }}
          style={{ top: '10%', left: '20%' }}
        />
        <motion.div
          className="absolute w-40 h-40 rounded-full bg-blue-500"
          animate={{ x: [-50, -150, 0], y: [50, -100, 50] }}
          transition={{ duration: 7, repeat: Infinity }}
          style={{ top: '60%', left: '70%' }}
        />
      </div>
    </motion.div>
  );
};

export default BackgroundAnimation;
