// Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import NavBar from '../components/NavBar';
import Footer from '../components/Footer';

// Animation for background objects
const floatingAnimation = {
  initial: { y: 0 },
  animate: {
    y: [0, 20, 0],
    transition: {
      duration: 4,
      repeat: Infinity,
      ease: 'easeInOut',
    },
  },
};

const Home = () => {
  return (
    <div className="min-h-screen flex flex-col">
      <NavBar />

      <main className="relative flex-1 overflow-hidden bg-gradient-to-r from-blue-200 via-blue-100 to-blue-300 p-8">
        {/* Background Animation */}
        <motion.div
          className="absolute inset-0 z-0 flex flex-wrap gap-4 opacity-20 pointer-events-none"
          initial="initial"
          animate="animate"
        >
          <motion.div
            className="w-32 h-32 rounded-full bg-blue-400"
            variants={floatingAnimation}
          />
          <motion.div
            className="w-20 h-20 rounded-full bg-blue-500"
            variants={floatingAnimation}
            style={{ animationDelay: '1s' }}
          />
          <motion.div
            className="w-24 h-24 rounded-full bg-blue-300"
            variants={floatingAnimation}
            style={{ animationDelay: '2s' }}
          />
          {/* Add more shapes as needed */}
        </motion.div>

        {/* Main Content */}
        <div className="relative z-10 hero flex flex-col items-center gap-8">
          <div className="hero-text text-center">
            <h2 className="text-3xl md:text-4xl font-bold text-blue-700">
              Building modern, scalable web applications
            </h2>
            <p className="mt-4 text-lg text-blue-600">
              Welcome! I'm Mohamed Ali, a developer passionate about creating clean and efficient code. Explore my work and skills below.
            </p>
            <Link to="/projects" className="mt-6 bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-300">
              View My Work
            </Link>
          </div>
          <div className="hero-image">
            <img src="/Images/chen-Qtu3hGinLF8-unsplash.jpg" alt="Coding at Work" className="max-w-sm rounded-lg shadow-lg" />
          </div>
        </div>
      </main>

      {/* Footer Section */}
      <Footer />
    </div>
  );
};

export default Home;
