// BackgroundAnimation.js
import React from 'react';
import { motion } from 'framer-motion';

const BackgroundAnimation = () => {
  return (
    <motion.div
      className="absolute inset-0 -z-10 bg-gradient-to-br from-purple-600 via-indigo-700 to-blue-800 opacity-20"
      initial={{ opacity: 0 }}
      animate={{ opacity: 0.2 }}
      transition={{ duration: 2 }}
    >
      <motion.div
        className="relative w-full h-full"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 0.3 }}
        transition={{ duration: 2, delay: 0.5 }}
      >
        {/* Floating circles */}
        <motion.div
          className="absolute w-40 h-40 rounded-full bg-purple-500 opacity-30"
          animate={{ x: [0, 200, -200, 0], y: [0, -100, 100, 0] }}
          transition={{ duration: 10, repeat: Infinity, repeatType: 'mirror' }}
          style={{ top: '20%', left: '15%' }}
        />
        <motion.div
          className="absolute w-60 h-60 rounded-full bg-indigo-600 opacity-60"
          animate={{ x: [0, -300, 300, 0], y: [0, 200, -200, 0] }}
          transition={{ duration: 12, repeat: Infinity, repeatType: 'mirror' }}
          style={{ top: '50%', left: '70%' }}
        />
      </motion.div>
    </motion.div>
  );
};

export default BackgroundAnimation;
