// src/pages/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Home = () => {
  return (
    <div className="min-h-screen flex flex-col justify-between bg-gradient-to-r from-blue-200 via-blue-100 to-blue-300 relative overflow-hidden">
      {/* Background Floating Elements */}
      <motion.div
        className="absolute top-20 left-10 w-32 h-32 bg-blue-500 rounded-full opacity-50"
        animate={{ y: [0, -20, 0], x: [0, 20, 0] }}
        transition={{ repeat: Infinity, duration: 8, ease: "easeInOut" }}
      />
      <motion.div
        className="absolute bottom-20 right-10 w-24 h-24 bg-blue-400 rounded-full opacity-60"
        animate={{ y: [0, 30, 0], x: [0, -30, 0] }}
        transition={{ repeat: Infinity, duration: 10, ease: "easeInOut" }}
      />
      <motion.div
        className="absolute top-40 right-32 w-20 h-20 bg-blue-600 rounded-full opacity-30"
        animate={{ y: [0, -25, 0], x: [0, 25, 0] }}
        transition={{ repeat: Infinity, duration: 12, ease: "easeInOut" }}
      />

      {/* Main Content */}
      <main className="hero flex flex-col items-center p-8 relative z-10">
        <div className="hero-content flex flex-col items-center gap-8">
          <div className="hero-text text-center">
            <h2 className="text-3xl md:text-4xl font-bold text-blue-700">
              Building modern, scalable web applications
            </h2>
            <p className="mt-4 text-lg text-blue-600">
              Welcome! I'm Mohamed Ali, a developer passionate about creating clean and efficient code. Explore my work and skills below.
            </p>
            <Link
              to="/projects"
              className="mt-6 bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-300"
            >
              View My Work
            </Link>
          </div>
          <div className="hero-image">
            <img src="/" alt="Coding at Work" className="max-w-sm rounded-lg shadow-lg" />
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-gray-800 text-white py-4 relative z-10">
        <div className="container mx-auto text-center">
          <div className="flex justify-center space-x-6 mb-2">
            <Link to="/about" className="text-blue-300 hover:underline">About</Link>
            <Link to="/projects" className="text-blue-300 hover:underline">Projects</Link>
            <Link to="/techstack" className="text-blue-300 hover:underline">Tech Stack</Link>
          </div>
          <p>&copy; 2024 Mohamed Ali. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Home;
