// src/pages/Home.js
import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="min-h-screen bg-gray-100">
      <main className="hero flex flex-col items-center bg-gradient-to-r from-blue-200 via-blue-100 to-blue-300 p-8">
        <div className="hero-content flex flex-col items-center gap-8">
          <div className="hero-text text-center">
            <h2 className="text-3xl md:text-4xl font-bold text-blue-700">
              Building modern, scalable web applications
            </h2>
            <p className="mt-4 text-lg text-blue-600">
              Welcome! I'm Mohamed Ali, a developer passionate about creating clean and efficient code. Explore my work and skills below.
            </p>
            <Link to="/projects" className="mt-6 bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-300">
              View My Work
            </Link>
          </div>
          <div className="hero-image">
            <img src="/Images/chen-Qtu3hGinLF8-unsplash.jpg" alt="Coding at Work" className="max-w-sm rounded-lg shadow-lg" />
          </div>
        </div>
      </main>
      <div className="flex justify-center space-x-6 py-8">
        <Link to="/about" className="text-blue-600 hover:underline">About</Link>
        <Link to="/techstack" className="text-blue-600 hover:underline">Tech Stack</Link>
        <Link to="/projects" className="text-blue-600 hover:underline">Projects</Link>
      </div>
    </div>
  );
};

export default Home;
